import React, { useState } from 'react'

const NewTaskCard = (props) => {

    const { handleSummit } = props

    const [queryTitle, setQueryTitle] = useState("")
    const [queryBody, setQueryBody] = useState("")

    const handleClick = () => {
        
        console.log("tarea agregada")
        handleSummit(queryTitle, queryBody)

    }

    return (
        <div className="newTask-card">
        <input
        className="newTask-title"
        placeholder="Título"
        onChange={(e) => {
            setQueryTitle(e.target.value)
        }}
        ></input>
        <input
        type= "text"
        className="newTask-body"
        placeholder="Descripción"
        onChange={(e) => {
            setQueryBody(e.target.value)
        } }
        ></input>
        <button
        className="btn btn-primary"
            onClick={ handleClick   }>
                Agregar tarea
        </button>
    </div>
    )
}

export default NewTaskCard
