import React, { useState } from 'react'
import NewTaskCard from './NewTaskCard'

const TaskCard = (props) => {

    const { title , id, handleUpdate, handleDelete, handleCheck, index, status} = props

    const [queryTitle, setQueryTitle] = useState("")
    const [edit, setEdit] = useState(false)
    const [check, setCheck] = useState(status)

    
   // console.log(status)
    return (
        <>
        
        {!edit && ( <div className="card mb-4 p-3">
                    <div className="d-flex justify-content-between">
                            <h5 className="card-title" >{title}</h5>                           
                            <input
                            className="form-check-input"
                            type="checkbox"
                            checked= {check}
                            onChange= { (e) => {handleCheck(index, e.target.checked)
                                setCheck(e.target.checked)
                            }} /> 
                        
                    </div>
                    <div className="task-card-button">
                            <button
                            className="btn btn-primary"
                            onClick={() => setEdit(true) } >
                                Editar
                            </button>

                            <button
                            className="btn btn-danger"
                            onClick= {() => handleDelete(id, index) } >
                                Eliminar
                            </button>
                        </div>
                     </div>)}

                { edit && ( <NewTaskCard
                
                />)}

        </>
    )
}

export default TaskCard
