import React, { useEffect, useState } from 'react'
import NewTaskCard from '../components/Cards/NewTaskCard'
import TaskCard from '../components/Cards/TaskCard'
import SearchBar from '../components/SearchBar/SearchBar'
import { httpGet } from '../libs/http'

const index = ({data}) => {

    const [tasks, setTasks] = useState(data)
    const [filtered, setFiltered] = useState(data)
    const [newTask, setNewTask] = useState(false)
    

    const perFiltered = (searchParam) => {
        let tasksFiltered = tasks
        if (searchParam.length == 0) {
            setFiltered(tasks)
        } else {

            tasksFiltered = tasksFiltered.filter((e) =>
                e.title.toLowerCase().replace(/ /g, '').match(
                    searchParam ? (searchParam).toLowerCase().replace(/ /g, '').replace(/#/g, '') : ''
                )
            )                
            setFiltered(tasksFiltered)            
        }
    }

    const handleSummit = async (title, body) => {        
        
        try {
            const response = await  fetch('https://my-json-server.typicode.com/lozcep/infotest/posts', {
            method: 'POST',
            body: JSON.stringify({
                title: title,
                body: body,
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
            })
        let json = await response.json()

        console.log(json)

        let newData = tasks

        await  newData.push(json)
        console.log(newData)

        setTasks(newData)
        setFiltered(newData)
        setNewTask(false)
            
        } catch (error) {
            console.log(error)
        }

    }

    const handleUpdate = async(title, body, newTitle, newBody, id, index, setEdit) => {

        if(newTitle !== title || newBody !== body ){

                try {        
                 
                    id < 3 ? id = id   : id = 1
                    
                const response = await fetch(`https://my-json-server.typicode.com/lozcep/infotest/posts/${id}`, {
                    method: 'PUT',
                    body: JSON.stringify({
                    title: newTitle,
                    body: newBody
                    }),
                    headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                    },
                })

              const json = await response.json()

                setTasks([...tasks, tasks[index]= json ])
                setFiltered([...filtered, filtered[index]= json ])            
                
                } catch (error) {
                    
                    console.log(error)
                }
        
        }else{
            console.log("no se ha hecho ninguna modificación")
        }
    }

    const handleDelete = async(id, index) => {
        setEdit(true)
        try {

            id < 3 ? id = id   : id = 1
            
           await fetch(`https://my-json-server.typicode.com/lozcep/infotest/posts/${id}`, {
                method: 'DELETE',
              });

                let newArr = tasks 
                newArr.splice( index ,1)  

                setTasks(newArr)    
                setFiltered(newArr) 
                setEdit(false)
                console.log(newArr)

        } catch (error) {
            console.log(error)
        }
    }


    return (
        <div>
            <h1>Lista de Tareas</h1>
            <SearchBar
            perFiltered={perFiltered} />

            <button
            onClick={ () => setNewTask(true)   }>
                Nueva tarea
            </button>

             { newTask && (<button
            onClick={ () => setNewTask(false) }>
                Cancelar
            </button>)}

            {newTask && (<NewTaskCard 
            setNewTask={setNewTask}
            handleSummit= {handleSummit}
            />) }

            {filtered && filtered.map( (item, index) =>( <TaskCard 
            key={`${item}-${index}`}
            index={index}
            id={item.id}
            title= {item.title}
            body= {item.body}
            handleUpdate={handleUpdate}
            handleDelete={handleDelete}
            />))}
            
        </div>
    )
}


export async function getServerSideProps(context) {
    const res = await fetch('https://my-json-server.typicode.com/lozcep/infotest/posts')
    const data = await res.json()
  
    
    if (!data) {
        console.log(data)
      return {
        redirect: {
          destination: '/',
          permanent: false,
        },
      }
    }
    return {
      props: {data},
    }
  }

export default index
