import React, { useEffect, useState } from 'react'
import NewTaskCard from '../components/Cards/NewTaskCard'
import TaskCard from '../components/Cards/TaskCard'
import SearchBar from '../components/SearchBar/SearchBar'

const { NEXT_PUBLIC_API_URL } = process.env
 
const index = ({data}) => {

    const [tasks, setTasks] = useState(data)
    const [filtered, setFiltered] = useState(data)
    const [newTask, setNewTask] = useState(false)
    const [loading, setLoading] = useState(false)

    console.log(NEXT_PUBLIC_API_URL)

    const perFiltered = (searchParam) => {
        let tasksFiltered = tasks
        if (searchParam.length == 0) {
            setFiltered(tasks)
        } else {

            tasksFiltered = tasksFiltered.filter((e) =>
                e.title.toLowerCase().replace(/ /g, '').match(
                    searchParam ? (searchParam).toLowerCase().replace(/ /g, '').replace(/#/g, '') : ''
                )
            )                
            setFiltered(tasksFiltered)            
        }
    }

    const handleSummit = async (title) => {        
        
        try {
            const response = await  fetch(`${NEXT_PUBLIC_API_URL}`, {
            method: 'POST',
            body: JSON.stringify({
                title: title,
                status: false
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
            })
        let json = await response.json()

        console.log(json)

        let newData = tasks

        await  newData.push(json)
        console.log(newData)

        setTasks(newData)
        setFiltered(newData)
        setNewTask(false)
            
        } catch (error) {
            console.log(error)
        }

    }

    const handleUpdate = async(title, newTitle, id, index, setEdit) => {

        if(newTitle !== title ){
                 setLoading(true)
                try {        
                 
                    id < 6 ? id = id   : id = 1
                    
                const response = await fetch(`${NEXT_PUBLIC_API_URL}/${id}`, {
                    method: 'PUT',
                    body: JSON.stringify({
                    title: newTitle,
                    }),
                    headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                    },
                })

                const json = await response.json()
                let newArr = tasks
                newArr[index] = json

                setTasks(newArr)
                setFiltered(newArr)            
                setLoading(false)
                setEdit(false)
                } catch (error) {
                    
                    console.log(error)
                }
        
        }else{
            console.log("no se ha hecho ninguna modificación")
        }
    }

    const handleDelete = async(id, index) => {
        setLoading(true)
        try {

         //   id < 6 ? id = id   : id = 1
            
           await fetch(`${NEXT_PUBLIC_API_URL}/${id}`, {
                method: 'DELETE',
              });

                let newArr = tasks 
                newArr.splice( index ,1)  

                setTasks(newArr)    
                setFiltered(newArr) 
                setLoading(false)
                console.log(newArr)

        } catch (error) {
            console.log(error)
            setLoading(false)
        }
    }

    const handleCheck = async (index, status) => {

                let newArr = tasks 
                newArr[index].status = status

                setTasks(newArr)    
                setFiltered(newArr) 
                
    }


    return (
        <div className="card p-3">
            <h1>Lista de Tareas</h1>
            <SearchBar
            perFiltered={perFiltered} />

            <div className="btn-group mb-3">

            <button
            className="btn btn-primary"
            onClick={ () => setNewTask(true)   }>
                Nueva tarea
            </button>

            <button
            className="btn btn-info"
            onClick={() => {
                setFiltered(tasks)
            }}
            >Todas</button>
            <button
            className="btn btn-warning"
            onClick={ () => {
                const arr = tasks.filter( (arr) => {
                    if (arr.status == false) return true 
                })
                console.log(arr)
                setFiltered(arr)
            } }>Pendientes</button>

            <button
            className="btn btn-success"
            onClick={ () => {
                const arr = tasks.filter( (arr) => {
                    if (arr.status == true) return true 
                })
                console.log(arr)
                setFiltered(arr)
            } }>Realizadas</button>

            </div>
            {newTask  && (<NewTaskCard            
            setNewTask={setNewTask}
            handleSummit= {handleSummit}
            setNewTask={setNewTask}
            />) }

            {filtered && !loading && filtered.map( (item, index) =>( <TaskCard 
            key={`${item}-${index}`}
            index={index}
            id={item.id}
            title= {item.title}
            status= {item.status}
            handleUpdate={handleUpdate}
            handleDelete={handleDelete}
            handleCheck={handleCheck}
            />))}
            
        </div>
    )
}


export async function getServerSideProps(context) {
    const res = await fetch(`${NEXT_PUBLIC_API_URL}`)
    const data = await res.json()
  
    
    if (!data) {
        console.log(data)
      return {
        redirect: {
          destination: '/',
          permanent: false,
        },
      }
    }
    return {
      props: {data},
    }
  }

export default index
