import React, { useState } from 'react'

const TaskCard = (props) => {

    const { title , body, id } = props

    const [queryTitle, setQueryTitle] = useState(title)
    const [queryBody, setQueryBody] = useState(body)
    const [edit, setEdit] = useState(false)

    const handleUpdate = async(newTitle, newBody, id) => {

        if(newTitle !== title || newBody !== body ){

            const response = await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
                method: 'PUT',
                body: JSON.stringify({
                  id: 1,
                  title: 'foo',
                  body: 'bar',
                  userId: 1,
                }),
                headers: {
                  'Content-type': 'application/json; charset=UTF-8',
                },
              })

              const json = await response.json()

            console.log(json)
        
        }else{
            console.log("no se ha hecho ninguna modificación")
        }
    }

    return (
        <>

        {!edit && ( <div className="taskCard">
            <h2>{title}</h2>
            <p>{body}</p>
            <div className="taskCardButtonDiv">
                <button
                onClick={() => setEdit(true) } >
                    Editar
                </button>
                <button>
                    Eliminar
                </button>
            </div>
            </div>)}

                { edit && ( <div className="newTaskCard">
                <input
                value={queryTitle}
                className="newTaskTitle"
                placeholder="Título"
                onChange={(e) => {
                    setQueryTitle(e.target.value)
                }}
                ></input>
                <input
                value={queryBody}
                type= "text"
                className="newTaskBody"
                placeholder="Descripción"
                onChange={(e) => setQueryBody(e.target.value) }
                ></input>
                <button
                onClick={() => handleUpdate(queryTitle, queryBody, id) }
                    >Actualizar
                </button>
                <button
                onClick={() =>  setEdit(false) }
                    >Cancelar
                </button>
            </div>)}

        </>
    )
}

export default TaskCard
