import React, { useState } from 'react'

const TaskCard = (props) => {

    const { title , body, id, handleUpdate, handleDelete, index, status} = props

    const [queryTitle, setQueryTitle] = useState(title)
    const [queryBody, setQueryBody] = useState(body)
    const [edit, setEdit] = useState(false)
    const [check, setCheck] = useState(status)

    return (
        <>

        {!edit && ( <div className="task-card">
            <h2>{title}</h2>
            <p>{body}</p>
            <div className="task-card-button">
                <button
                onClick={() => setEdit(true) } >
                    Editar
                </button>
                <button
                onClick= {() => handleDelete(id, index) } >
                    Eliminar
                </button>
                <input
                type="checkbox"
                checked= {check}
                onChange= { (e) => {
                           setCheck(e.target.checked)
                }} />
            </div>
            </div>)}

                { edit && ( <div className="newTask-card">
                <input
                value={queryTitle}
                className="newTask-title"
                placeholder="Título"
                onChange={(e) => {
                    setQueryTitle(e.target.value)
                }}
                ></input>
                <input
                value={queryBody}
                type= "text"
                className="newTask-body"
                placeholder="Descripción"
                onChange={(e) => setQueryBody(e.target.value) }
                ></input>
                <button
                onClick={() => handleUpdate(title, body, queryTitle, queryBody, id, index, setEdit) }
                    >Actualizar
                </button>
                <button
                onClick={() =>  setEdit(false) }
                    >Cancelar
                </button>
            </div>)}

        </>
    )
}

export default TaskCard
