import React, { useState } from 'react'
import CheckBox from '../CheckBox/CheckBox'

const TaskCard = (props) => {

    const { title , id, handleUpdate, handleDelete, handleCheck, index, status} = props

    const [queryTitle, setQueryTitle] = useState(title)
    const [edit, setEdit] = useState(false)

   /*  check != status && setCheck(status)
    queryTitle != title && setQueryTitle(title) */

    return (
        <>
        
        {!edit && ( <div className="card mb-4 p-3">
                    <div className="d-flex justify-content-between">
                            <h5 className="card-title" >{title}</h5>                           
                            <CheckBox 
                            status={status}
                            handleCheck={handleCheck}
                            /> 
                        
                    </div>
                    <div className="task-card-button">
                            <button
                            className="btn btn-primary"
                            onClick={() => setEdit(true) } >
                                Editar
                            </button>

                            <button
                            className="btn btn-danger"
                            onClick= {() => handleDelete(id, index) } >
                                Eliminar
                            </button>
                        </div>
                     </div>)}

                { edit && ( <div className="card mb-3 p-3">
                <input
                value={queryTitle}
                className="form-control form-control-lg"
                onChange={(e) => {
                    setQueryTitle(e.target.value)
                }}
                />             
                <div className="task-card-button">
                <button
                className="btn btn-primary"
                onClick={() => handleUpdate(title, queryTitle, id, index, setEdit) }
                >Actualizar
                </button>
                <button
                className="btn btn-danger"
                onClick={() =>  setEdit(false) }
                >Cancelar
                </button>
                </div>
            </div>)}

        </>
    )
}

export default TaskCard
