import React, { useEffect } from 'react'
import TaskCard from '../components/Cards/TaskCard'
import { httpGet } from '../libs/http'

const alltask = ({stars}) => {


    return (
        <div>
            <h1>hola mundo</h1>
            <TaskCard />
            Next stars: {stars}
        </div>
    )
}

alltask.getInitialProps = async (ctx) => {
    
    let data = 0

    const res = await fetch('https://my-json-server.typicode.com/lozcep/infotest/posts')
    .then((response) => response.json())
    .then((json) => data = json)
    
    console.log(data)

    return { stars: res }
  }

export default alltask
