import React, { useEffect, useState } from 'react'
import TaskCard from '../components/Cards/TaskCard'
import SearchBar from '../components/SearchBar/SearchBar'
import { httpGet } from '../libs/http'

const index = ({data}) => {

    const [tasks, setTasks] = useState(data[0])
    const [filtered, setFiltered] = useState(data[0])

    const perFiltered = (searchParam) => {
        let tasksFiltered = filtered
        if (searchParam.length == 0) {
            setFiltered(tasks)
        } else {

            tasksFiltered = tasksFiltered.filter((e) =>
                e.name.toLowerCase().replace(/ /g, '').match(
                    searchParam ? (searchParam).toLowerCase().replace(/ /g, '').replace(/#/g, '') : ''
                )
            )

            setFiltered(tasksFiltered)
            setCompanies({
                ...companies,
                pageCount: Math.ceil(tasksFiltered.length / 12)
            })
        }
    }

    return (
        <div>
            <h1>Lista de Tareas</h1>
            <SearchBar />
            {tasks && data.map( (item, index) =>( <TaskCard 
            key={`${item}-${index}`}
            title= {item.title}
            body= {item.body}
            />))
            
            }
            {console.log(data)}
            
        </div>
    )
}


export async function getServerSideProps(context) {
    const res = await fetch('https://my-json-server.typicode.com/lozcep/infotest/posts')
    const data = await res.json()
  
    
    if (!data) {
        console.log(data)
      return {
        redirect: {
          destination: '/',
          permanent: false,
        },
      }
    }
    return {
      props: {data}, // will be passed to the page component as props
    }
  }

export default index
