import React, { useState } from 'react'

const NewTaskCard = (props) => {

    const { setNewTask, handleSummit } = props

    const [queryTitle, setQueryTitle] = useState("")
    const [queryBody, setQueryBody] = useState("")

    const handleClick = () => {
        
        console.log("tarea agregada")
        //setNewTask(false)
        handleSummit(queryTitle, queryBody)

    }

    return (
        <div className="newTaskCard">
        <input
        className="newTaskTitle"
        placeholder="Título"
        onChange={(e) => {
            setQueryTitle(e.target.value)
        }}
        ></input>
        <input
        type= "text"
        className="newTaskBody"
        placeholder="Descripción"
        onChange={(e) => {
            setQueryBody(e.target.value)
        } }
        ></input>
        <button
            onClick={ handleClick   }>
                Agregar tarea
        </button>
    </div>
    )
}

export default NewTaskCard
