import React, { useEffect, useState } from 'react'
import NewTaskCard from '../components/Cards/NewTaskCard'
import TaskCard from '../components/Cards/TaskCard'
import SearchBar from '../components/SearchBar/SearchBar'
import { httpGet } from '../libs/http'

const index = ({data}) => {

    const [tasks, setTasks] = useState(data)
    const [filtered, setFiltered] = useState(data)
    const [newTask, setNewTask] = useState(false)
    const [loading, setLoading] = useState(false)
    

    const perFiltered = (searchParam) => {
        let tasksFiltered = tasks
        if (searchParam.length == 0) {
            setFiltered(tasks)
        } else {

            tasksFiltered = tasksFiltered.filter((e) =>
                e.title.toLowerCase().replace(/ /g, '').match(
                    searchParam ? (searchParam).toLowerCase().replace(/ /g, '').replace(/#/g, '') : ''
                )
            )                
            setFiltered(tasksFiltered)            
        }
    }

    const handleSummit = async (title, body) => {        
        
        try {
            const response = await  fetch('https://my-json-server.typicode.com/lozcep/infotest/posts', {
            method: 'POST',
            body: JSON.stringify({
                title: title,
                body: body,
                status: false
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
            })
        let json = await response.json()

        console.log(json)

        let newData = tasks

        await  newData.push(json)
        console.log(newData)

        setTasks(newData)
        setFiltered(newData)
        setNewTask(false)
            
        } catch (error) {
            console.log(error)
        }

    }

    const handleUpdate = async(title, body, newTitle, newBody, id, index, setEdit) => {

        if(newTitle !== title || newBody !== body ){
            setLoading(true)
                try {        
                 
                    id < 6 ? id = id   : id = 1
                    
                const response = await fetch(`https://my-json-server.typicode.com/lozcep/infotest/posts/${id}`, {
                    method: 'PUT',
                    body: JSON.stringify({
                    title: newTitle,
                    body: newBody
                    }),
                    headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                    },
                })

                const json = await response.json()
                let newArr = tasks
                newArr[index] = json

                setTasks(newArr)
                setFiltered(newArr)            
                setLoading(false)
                setEdit(false)
                } catch (error) {
                    
                    console.log(error)
                }
        
        }else{
            console.log("no se ha hecho ninguna modificación")
        }
    }

    const handleDelete = async(id, index) => {
        setLoading(true)
        try {

            id < 6 ? id = id   : id = 1
            
           await fetch(`https://my-json-server.typicode.com/lozcep/infotest/posts/${id}`, {
                method: 'DELETE',
              });

                let newArr = tasks 
                newArr.splice( index ,1)  

                setTasks(newArr)    
                setFiltered(newArr) 
                setLoading(false)
                console.log(newArr)

        } catch (error) {
            console.log(error)
            setLoading(false)
        }
    }

    const handleCheck = async (index, status) => {
        
                setLoading(true)        
                let newArr = tasks 
                newArr[index].status = status

                setTasks(newArr)    
                setFiltered(newArr) 
                setLoading(false)
                console.log(newArr)

    }


    return (
        <div>
            <h1>Lista de Tareas</h1>
            <SearchBar
            perFiltered={perFiltered} />

            <button
            onClick={ () => setNewTask(true)   }>
                Nueva tarea
            </button>

             { newTask && (<button
            onClick={ () => setNewTask(false) }>
                Cancelar
            </button>)}

            <button>Todas</button>
            <button
            onClick={ () => {
                const arr = filtered.filter( (arr) => {
                    if (arr.status == false) return true 
                })
                console.log(arr)
                setFiltered(arr)
            } }>Pendientes</button>
            <button>Realizadas</button>

            {newTask && (<NewTaskCard 
            setNewTask={setNewTask}
            handleSummit= {handleSummit}
            />) }

            {filtered && filtered.map( (item, index) =>( <TaskCard 
            key={`${item}-${index}`}
            index={index}
            id={item.id}
            title= {item.title}
            body= {item.body}
            status= {item.status}
            handleUpdate={handleUpdate}
            handleDelete={handleDelete}
            handleCheck={handleCheck}
            />))}
            
        </div>
    )
}


export async function getServerSideProps(context) {
    const res = await fetch('https://my-json-server.typicode.com/lozcep/infotest/posts')
    const data = await res.json()
  
    
    if (!data) {
        console.log(data)
      return {
        redirect: {
          destination: '/',
          permanent: false,
        },
      }
    }
    return {
      props: {data},
    }
  }

export default index
