import React, { useEffect, useState } from 'react'
import NewTaskCard from '../components/Cards/NewTaskCard'
import TaskCard from '../components/Cards/TaskCard'
import SearchBar from '../components/SearchBar/SearchBar'
import { httpGet } from '../libs/http'

const index = ({data}) => {

    const [tasks, setTasks] = useState(data)
    const [filtered, setFiltered] = useState(data)
    const [newTask, setNewTask] = useState(false)
    

    const perFiltered = (searchParam) => {
        let tasksFiltered = tasks
        if (searchParam.length == 0) {
            setFiltered(tasks)
        } else {

            tasksFiltered = tasksFiltered.filter((e) =>
                e.title.toLowerCase().replace(/ /g, '').match(
                    searchParam ? (searchParam).toLowerCase().replace(/ /g, '').replace(/#/g, '') : ''
                )
            )                
            setFiltered(tasksFiltered)            
        }
    }

    const handleSummit = async (title, body) => {        
        
        try {
            const response = await  fetch('https://my-json-server.typicode.com/lozcep/infotest/posts', {
            method: 'POST',
            body: JSON.stringify({
                title: title,
                body: body,
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
            })
        let json = await response.json()

        console.log(json)

        let newData = tasks

        await  newData.push(json)
        console.log(newData)

        setTasks(newData)
        setFiltered(newData)
        setNewTask(false)
            
        } catch (error) {
            console.log(error)
        }

    }

    const handleUpdate = async(title, body, newTitle, newBody, id, setEdit) => {

        if(newTitle !== title || newBody !== body ){


                try {        
                    
                const response = await window.fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
                    method: 'PUT',
                    body: JSON.stringify({
                    title: newTitle,
                    body: newBody
                    }),
                    headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                    },
                })

              const json = await response.json()

                let newArr = tasks
                newArr[id-1] = json
                console.log(json)
                setFiltered(newArr)
                setTasks(newArr)
                setEdit(false)
                } catch (error) {
                    
                    console.log(error)
                }
        
        }else{
            console.log("no se ha hecho ninguna modificación")
        }
    }


    return (
        <div>
            <h1>Lista de Tareas</h1>
            <SearchBar
            perFiltered={perFiltered} />

            <button
            onClick={ () => setNewTask(true)   }>
                Nueva tarea
            </button>

             { newTask && (<button
            onClick={ () => setNewTask(false) }>
                Cancelar
            </button>)}

            {newTask && (<NewTaskCard 
            setNewTask={setNewTask}
            handleSummit= {handleSummit}
            />) }

            {tasks && filtered.map( (item, index) =>( <TaskCard 
            key={`${item}-${index}`}
            id={item.id}
            title= {item.title}
            body= {item.body}
            handleUpdate={handleUpdate}
            />))}
            
        </div>
    )
}


export async function getServerSideProps(context) {
    const res = await fetch('https://my-json-server.typicode.com/lozcep/infotest/posts')
    const data = await res.json()
  
    
    if (!data) {
        console.log(data)
      return {
        redirect: {
          destination: '/',
          permanent: false,
        },
      }
    }
    return {
      props: {data},
    }
  }

export default index
