import React, { useState } from 'react'

const TaskCard = (props) => {

    const { title , body } = props

    const [edit, setEdit] = useState(false)
    const [queryTitle, setQueryTitle] = useState("")
    const [queryBody, setQueryBody] = useState("body")

    return (
        <div className="taskCard">
            <h2>{title}</h2>
            <p>{body}</p>
            <div className="taskCardButtonDiv">
                <button
                onClick={setEdit(true)}>
                    Editar
                </button>
                <button>
                    Eliminar
                </button>
            </div>

                { edit && ( <div className="newTaskCard">
                <input
                value={queryTitle}
                className="newTaskTitle"
                placeholder="Título"
                onChange={(e) => {
                    setQueryTitle(e.target.value)
                }}
                ></input>
                <input
                value={queryBody}
                type= "text"
                className="newTaskBody"
                placeholder="Descripción"
                onChange={(e) => {
                    setQueryBody(e.target.value)
                } }
                ></input>
                <button
                    >actualizar
                </button>
            </div>)}

        </div>
    )
}

export default TaskCard
