import React, { useState } from 'react'

const TaskCard = (props) => {

    const { title , body, id, setTasks,setFiltered, tasks } = props

    const [queryTitle, setQueryTitle] = useState(title)
    const [queryBody, setQueryBody] = useState(body)
    const [edit, setEdit] = useState(false)

    return (
        <>

        {!edit && ( <div className="taskCard">
            <h2>{title}</h2>
            <p>{body}</p>
            <div className="taskCardButtonDiv">
                <button
                onClick={() => setEdit(true) } >
                    Editar
                </button>
                <button>
                    Eliminar
                </button>
            </div>
            </div>)}

                { edit && ( <div className="newTaskCard">
                <input
                value={queryTitle}
                className="newTaskTitle"
                placeholder="Título"
                onChange={(e) => {
                    setQueryTitle(e.target.value)
                }}
                ></input>
                <input
                value={queryBody}
                type= "text"
                className="newTaskBody"
                placeholder="Descripción"
                onChange={(e) => setQueryBody(e.target.value) }
                ></input>
                <button
                onClick={() => handleUpdate(title, body, queryTitle, queryBody, id) }
                    >Actualizar
                </button>
                <button
                onClick={() =>  setEdit(false) }
                    >Cancelar
                </button>
            </div>)}

        </>
    )
}

export default TaskCard
